mapper.select:
		SELECT id, mname, tel, email, postcode, address1, address2,
		fname, r
		from(
		SELECT id, mname, tel, email, postcode, address1, address2,
		fname, rownum r
		from(
		SELECT id, mname, tel, email, postcode, address1,
		address2, fname
		FROM member



					mname like '%'||#{word}||'%'


					id like '%'||#{word}||'%'


					email like '%'||#{word}||'%'




		)

                )where r >= #{sno} and r <= #{eno}

mapper.update:
update member
set fname = #{fname}
where id = #{id}

mapper.select:
select
grade from member
where id = #{id}

select
count(*) from member
where id = #{id}
and password = #{password}

SELECT COUNT(*)
FROM member
WHERE id=#{id}

SELECT COUNT(*)
FROM member
WHERE email=#{email}
